import {Component, OnInit} from '@angular/core';
import {DnmaService} from "../../service/dnma.service";

@Component({
  selector: 'app-eb2-form',
  templateUrl: './eb2-form.component.html',
  styleUrls: ['./eb2-form.component.css']
})
export class Eb2FormComponent implements OnInit {
  protected readonly Object = Object;
  dataset!: any;

  constructor(private dnmaService: DnmaService) {
  }

  ngOnInit(): void {

    this.byMonth()
    // this.byYear()

  }

  byYear() {
    this.dnmaService.findByYearAndUai().subscribe(
      (response: any) => {
        this.dataset = response.results;
        console.log(this.dataset);
      }, error => {
        console.log(error);

      }
    );
  }

  byMonth() {
    this.dnmaService.findByMonth().subscribe(
      (response: any) => {
        this.dataset = response.results;
        this.dataset.sort((a: any, b: any) => a["month(debutsemaine)"] - b["month(debutsemaine)"]);
        console.log(this.dataset);
      }, error => {
        console.log(error);

      }
    );
  }

}
<div class="container mt-5">
  <h2 class="text-center">Statistiques d'utilisation par type d'appareil</h2>
  <h1>Data Aggregation Form</h1>

  <form>
    <div class="mb-3">
      <label for="uaiInput" class="form-label">UAI:</label>
      <input type="text" class="form-control" id="uaiInput" placeholder="Enter UAI">
    </div>

    <div class="mb-3">
      <label for="criteriaSelect" class="form-label">Select Criteria:</label>
      <select class="form-select" id="criteriaSelect">
        <option value="year">Année</option>
        <option value="month">Mois</option>
      </select>
    </div>

    <button type="button" class="btn btn-primary" onclick="submitForm()">Submit</button>
  </form>

</div>
