import {Component, OnInit} from '@angular/core';
import {DnmaService} from "../../service/dnma.service";
import {FormBuilder, FormGroup} from "@angular/forms";

@Component({
  selector: 'app-eb2-form',
  templateUrl: './eb2-form.component.html',
  styleUrls: ['./eb2-form.component.css']
})
export class Eb2FormComponent implements OnInit {  dataForm!: FormGroup;
  constructor(private fb: FormBuilder) {
    this.dataForm = this.fb.group({
      uai: [''],
      criteria: ['']
    });
  }

  ngOnInit(): void {


  }
  submitForm() {
    // Handle form submission logic here
    console.log(this.dataForm.value);
  }

}
<div class="container mt-5">
  <h2 class="text-center">Statistiques d'utilisation par type d'appareil</h2>
  <h1>Data Aggregation Form</h1>

  <form [formGroup]="dataForm" (ngSubmit)="submitForm()">
    <div class="mb-3">
      <label for="uaiInput" class="form-label">UAI:</label>
      <input type="text" class="form-control" id="uaiInput" formControlName="uai" placeholder="Enter UAI">
    </div>

    <div class="mb-3">
      <label for="criteriaSelect" class="form-label">Select Criteria:</label>
      <select class="form-select" id="criteriaSelect" formControlName="criteria">
        <option value="year">Année</option>
        <option value="month">Mois</option>
      </select>
    </div>

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>

</div>
