import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormGroup} from "@angular/forms";
import {Router} from "@angular/router";

@Component({
  selector: 'app-eb3-form',
  templateUrl: './eb3-form.component.html',
  styleUrls: ['./eb3-form.component.css']
})
export class Eb3FormComponent implements OnInit {
  formulaire!: FormGroup;

  constructor(private fb: FormBuilder,
              private router: Router) {
    this.formulaire = this.fb.group({
      uai: [''],
      year: ['']
    });
  }

  ngOnInit(): void {


  }

  submitForm() {
    // Handle form submission logic here
    console.log(this.formulaire.value);
    const uaiValue = this.formulaire.get('uai')?.value;
    const year = this.formulaire.get('year')?.value;
    this.router.navigate(['/eb2-table', uaiValue, year]);
  }

}
<div class="container mt-5">
  <h2 class="text-center">Statistiques d'utilisation par type d'appareil</h2>
  <h1>Data Aggregation Form</h1>

  <form [formGroup]="formulaire" (ngSubmit)="submitForm()">
    <div class="mb-3">
      <label for="uaiInput" class="form-label">UAI:</label>
      <input type="text" class="form-control" id="uaiInput" formControlName="uai" placeholder="Enter UAI">
    </div>

    <div class="mb-3">
      <label for="year" class="form-label">Select choix:</label>

      <input type="number" class="form-control" id="year" formControlName="year" placeholder="Enter year">

    </div>

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>

</div>
